var __ma_notificationTime = 0; var __ma_notificationInterval = null; var __mc_confirmDef = $.Deferred(); function MaterialNotify(message, type, non_autohide) {
    var nonautohide = non_autohide || !1; $.when(__maGenerateNotif(message, type)).done(function () {
        __initNotifEvents(); if (nonautohide === !1) { __ma_notificationInterval = setInterval(__notiftimerIncrement, 1000) }
        $('.ma-bg').fadeIn(100); $('.ma-bg').css('background-color', '#212121'); $('.ma-maincon').css('opacity', '1'); $('.m-alert').css('opacity', '1'); $('.m-alert').css('transform', 'translateY(0)')
    })
}
function MaterialConfirm(data) { var Data = data || { title: '', message: 'Are you sure?' }; __mc_confirmDef = $.Deferred(); $.when(__mcGenerateConfirm(Data)).done(function () { __initConfirmEvents(); $('.mc-bg').fadeIn(100); $('.mc-bg').css('background-color', '#212121'); $('.m-confirm').css('opacity', '1'); $('.m-confirm').css('transform', 'translateY(0)'); $('.mc-btncon.mc-btnyes').focus() }); return __mc_confirmDef.promise() }
function __maGenerateNotif(message, type) { var __materialNotifHtml = ""; __materialNotifHtml = '<div class="ma-bg"></div>'; __materialNotifHtml += '<div class="ma-maincon"><div class="ma-secon col-md-4">'; __materialNotifHtml += '<div class="m-alert col-sm-12">'; __materialNotifHtml += '<span class="ma-closebtn">&times;</span>'; __materialNotifHtml += '<div class="row">'; __materialNotifHtml += '<div class="ma-leftp ' + type.toLowerCase() + ' col-sm-4"></div>'; __materialNotifHtml += '<div class="ma-rightp ' + type.toLowerCase() + ' col-sm-8">'; __materialNotifHtml += '<span class="ma-header"></span>'; __materialNotifHtml += '<hr class="ma-divider" />'; __materialNotifHtml += '<p class="ma-message">' + message + '</p>'; __materialNotifHtml += '</div></div></div></div></div>'; $('body').prepend(__materialNotifHtml) }
function __mcGenerateConfirm(data) {
    var __materialConfirmHtml = ""; var title = data.title; var message = data.message; var okButtonText = data.okButtonText || 'Yes'; var okButtonTextColor = data.okButtonTextColor || 'rgb(34, 34, 34)'; var cancelButtonText = data.cancelButtonText || 'No'; var cancelButtonTextColor = data.cancelButtonTextColor || 'rgb(34, 34, 34)'; var headerColor = data.headerColor || 'rgb(34, 34, 34)'; var titleColor = data.titleColor || 'rgb(34, 34, 34)'; if (data.colorTheme) { headerColor = data.colorTheme; cancelButtonTextColor = data.colorTheme; okButtonTextColor = data.colorTheme }
    __materialConfirmHtml = '<div class="mc-bg"></div>'; __materialConfirmHtml += '<div class="mc-con"><div class="mc-in  col-md-4">'; __materialConfirmHtml += '<div class="m-confirm col-sm-12">'; __materialConfirmHtml += '<span class="mc-closebtn">&times;</span>'; __materialConfirmHtml += '<div class="mc-top col-sm-12" style="background-color: ' + headerColor + '"></div>'; __materialConfirmHtml += '<div class="mc-bot info col-sm-12">'; if (title != undefined) { __materialConfirmHtml += '<div class="mc-title" style="color: ' + titleColor + ';">' + title + '</div>' }
    if (message != undefined) { __materialConfirmHtml += '<p class="mc-message">' + message + '</p>' } else { __materialConfirmHtml += '<p class="mc-message">Are you sure?</p>' }
    __materialConfirmHtml += '<div class="mc-divider"></div>'; __materialConfirmHtml += '<div class="mc-btncon">'; __materialConfirmHtml += '<div class="mc-btnno"><a class="button-text" style="color: ' + cancelButtonTextColor + ';">' + cancelButtonText + '</a></div>'; __materialConfirmHtml += '<div class="mc-btnyes"><a class="button-text" style="color: ' + okButtonTextColor + ';">' + okButtonText + '</a></div>'; __materialConfirmHtml += '</div></div></div></div></div>'; $('body').prepend(__materialConfirmHtml)
}
function __initNotifEvents() { $('.ma-closebtn, .ma-bg, .ma-secon, .ma-maincon').unbind().click(function () { __maClose() }) }
function __initConfirmEvents() {
    $('.mc-con .mc-closebtn, .mc-bg, .mc-con').unbind().click(function (e) {
        __mcClose(); __mc_confirmDef.reject()
        e.stopPropagation()
    }); $('.m-confirm').unbind().click(function (e) { e.stopPropagation() }); $('.mc-bg, .mc-con .mc-btnyes').unbind().click(function () { __mcClose(); __mc_confirmDef.resolve() }); $('.mc-bg, .mc-con .mc-btnno').unbind().click(function () { __mcClose(); __mc_confirmDef.reject() })
}
function __notiftimerIncrement() { __ma_notificationTime += 1; if (__ma_notificationTime == 2) { __maClose() } }
function __maClose() { $('.m-alert').css('transform', 'translateY(-30px)'); $('.ma-secon').css('opacity', '0'); setTimeout(function () { $('.ma-secon').css('z-index', '0'); $('.ma-bg').css('background-color', 'transparent'); setTimeout(function () { $('.ma-secon').css('display', 'none'); $('.ma-maincon').remove(); $('.ma-secon').remove(); $('.ma-bg').remove() }, 100) }, 100); clearInterval(__ma_notificationInterval); __ma_notificationTime = 0 }
function __mcClose() { $('.m-confirm').css('transform', 'translateY(-30px)'); $('.m-confirm').css('opacity', '0'); setTimeout(function () { $('.mc-con').css('opacity', '0'); $('.mc-bg').css('background-color', 'transparent'); setTimeout(function () { $('.mc-con').remove(); $('.mc-bg').remove() }, 100) }, 100) }